import React, { useState } from "react";
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const FreqQuestion = () =>{
    let [show, setShow] = useState(false);

    const showPara = () =>{
        setShow(true)
    }

    return(
        <div className="col-12">
            <div className="Freq_Question">
            <div className="Fre_heading">
            <h4 onClick={showPara}>What is SchoolWay, and how does it differ from traditional school management systems?  </h4>
            <ExpandMoreIcon />
            </div>
            {show && (<p>SchoolWay is a School Management Software as a Service (SaaS) that offers a modern and cloud-based approach to managing educational institutions. Unlike traditional systems, SchoolWay is accessible online, requires no local installations, and offers a more flexible and scalable solution.</p>)}


            </div>
        </div>

    )
}

export default FreqQuestion;
