import React, { useState } from "react";
import "./index.css";

import AddIcon from '@mui/icons-material/Add';

let MainComp = (props) =>{
    const [data, setdata] = useState({
        title: "",
        content : ""
    });
    
    const inputEvent = (event)=>{
        let name = event.target.name;
        let value = event.target.value;
        setdata((prev)=>{
            return{
                ...prev,
                [name]: value
            }

        })
    };

    const ClickEvent = (event) =>{
        props.passNote(data);
        event.preventDefault();
        setdata({
            title: "",
            content : ""
        })

    };
    
    let [expand, setExpand] = useState(false);
    const ExpandEvent = () =>{
        setExpand(true)
    }

    const backNormal = () =>{
        setExpand(false)
    }

    
    return( <>
              <div className="Main_comp" onDoubleClick={backNormal}>
                  <form>
                        {expand?
                        <input type="text" name="title" value={data.title} onChange={inputEvent} placeholder="Title" /> : null}

                        <textarea rows="" cols="" name="content" value={data.content} onChange={inputEvent} placeholder="Write a note..."  onClick={ExpandEvent}/> 

                        {expand?
                        <button onClick={ClickEvent} className="btn_style">
                            <AddIcon/>
                        </button> : null}
                  </form>
              </div>
            </>)

}
export default MainComp;
