import React, { useState } from "react";
import "./index.css"

let Accordion = ({Ques, Answer}) =>{
    let [show, setShow] = useState(false);
    let ShowAnswer = () =>{
        setShow(!show)
    }
    return(<>
        <div className="Accordion_Style col-6">
        <p onClick={ShowAnswer}>{show? "➖" : "➕"}</p>
        <h2>{Ques}</h2>
        </div>
        {show && <p>{Answer}</p>}

        
        
    </>)

}
export default Accordion;
