import React, { useState } from "react";
// import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const FreqQuestion = () =>{
    let [show, setShow] = useState(false);

    const showPara = ({QHeading, QAnswer}) =>{
        setShow(!show)
    }

    return(
        <div className="col-12">
            <div className="Freq_Question">
            <div className="Fre_heading">
            <h4 onClick={showPara}>{QHeading} </h4>
            <p>{!show? (<i class="fa fa-angle-down" aria-hidden="true"></i>) : (<i class="fa fa-angle-up" aria-hidden="true"></i>)}</p>
            </div>
            {show && (<p>{QAnswer}</p>)}


            </div>
        </div>

    )
}

export default FreqQuestion;
