import React, { useEffect, useState } from "react";

const App = () =>{
    let [num, setNum] = useState(0);

    useEffect(()=>{
        alert ("Welcome! Asad Rehman {num}")

    })

    let ClickEvent =() =>{
        setNum(num+1);
        

    }

    return(
        <>
            <h1>{num}</h1>
            <button onClick={ClickEvent}>Click Me </button>
        </>
    )

}

export default App;
