import React, { useState } from 'react';

import AddIcon from '@mui/icons-material/Add';
import "./index.css";
import ToDo from './Todo';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"






function App() {
  const [List, setlist] = useState("");
  const [addlist, setaddlist] = useState([]);

  let InputEvent = (event)=>{
    setlist(event.target.value)

  };

  let AddEvent = () =>{
    setaddlist((oldArray)=>{
      return[...oldArray,List]

    })
    setlist("")
  };
  let deletItem = (id)=>{
    setaddlist((oldArray)=>{
      return oldArray.filter((element, index)=>{
        return id !== index

      })

    })
  }



  return (
    <>
    <div className='main_coontainer'>
      <div className='app'>
        <h1>ToDo List</h1><br/>
        <input type='text' placeholder='Enter Your List'value={List} onChange={InputEvent}/>
        <button onClick={AddEvent} className='btn btn-success' style={{borderRadius: "100%", borderColor: "transparent"}}> <AddIcon /> </button>
        <ul>
        { addlist.map((element, index)=>{
          return(<ToDo 
          key = {index}
          id = {index}
          text={element}
          onselect= {deletItem}
          />)
        })}

        </ul>
      </div>
    </div>

    </>
  );
}

export default App;
