import React, { useState } from 'react';

import AddIcon from '@mui/icons-material/Add';
import "./index.css"




function App() {
  const [List, setlist] = useState("");
  const [addlist, setaddlist] = useState("");

  let InputEvent = (event)=>{
    setlist(event.target.value)

  }

  let AddEvent = () =>{
    setaddlist(List)
  }



  return (
    <>
    <div className='main_coontainer'>
      <div className='app'>
        <h1>ToDo List</h1><br/>
        <input type='text' placeholder='Enter Your List'value={List} onChange={InputEvent}/>
        <button onClick={AddEvent}> <AddIcon /> </button>
        <ul>
          <li>{List}</li>
        </ul>
      </div>
    </div>

    </>
  );
}

export default App;
