import React, { useState } from "react";
import "./index.css";

import AddIcon from '@mui/icons-material/Add';

let MainComp = () =>{
    const [data, setdata] = useState({
        title: "",
        content : ""
    });
    const inputEvent = (event)=>{
        let name = event.target.name;
        let value = event.target.value;
        setdata((prev)=>{
            return{
                ...prev,
                [name]: value
            }

        })
    }
    
    return(<>
        <div className="Main_comp">
            <form>
            <input type="text" name="title" value={data.title} onChange={inputEvent} placeholder="Title" />
            <textarea rows="" column="" name="content" value={data.content} onChange={inputEvent} placeholder="Write a note..." /> 
            <button onClick={ClickEvent} className="btn_style">
                <AddIcon/>
            </button>
            </form>
        </div>
    </>)

}
export default MainComp;
