import React, { useEffect, useState } from "react";
import axios from "axios";

const CompA = () =>{
    let [num, setnum] = useState("1");
    let [Name, setName] = useState("");
    let [moves, setMoves] = useState("");

    useEffect(()=>{
        
        async function getData(){
            const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/ditto/${num}`);
            console.log(res.data);
            setName(res.data.name);
            setMoves(res.data.moves.length);
        }
        getData();
    }, [num]);

    return(
        <>
            <h1>You're number is <span style={{color: "red"}}> {num} </span>  </h1>
            <h1>
            You're name is <span style={{color: "red"}}>{Name}</span> 
            </h1>
            <h1>
            i have <span style={{color: "red"}}>{moves} moves</span> 
            </h1>

            <select value={num} onChange={(event) =>{
        setnum(event.target.value)
    }}>
                <option value="1">1</option>
                <option value="25">25</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
            </select>
        </>
    )

}
export default CompA;
