import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Contact = () =>{
    let [data, setData] = useState({
        name : "",
        father : "",
        email : "",
        phone : "",
        message : ""

    })


    let [get, setGet] = useState()
   

    let InputEvent = (event)=>{
        const value = event.target.value;
        const name = event.target.name;
        setData((prev)=>{
            return {
                ...prev, 
                [name]:value
            }

        })
    }
    let Onsubmit = (event)=>{
        event.preventDefault();
        setGet(data)
    }

    
    return(
        <>
         <div className="container mt-2 ">
            <h1 className="text-center">Contact Us {get}</h1>
            <div className="row">
                <div className="col-lg-4 col-md-6 col-sm-10 mx-auto  ">
                <form onSubmit={Onsubmit}>
                <div class="mb-3">
                   <label for="exampleFormControlInput1" class="form-label">Name</label>
                    <input type="text" class="form-control" id="exampleFormControlInput1" name="name" value={data.name} onChange={InputEvent} placeholder="Enter Your Name"/>
                </div>
                <div class="mb-3">
                   <label for="exampleFormControlInput1" class="form-label">Father's Name</label>
                    <input type="text" class="form-control" id="exampleFormControlInput1" name="father" value={data.father} onChange={InputEvent} placeholder="Enter Your Father's Name"/>
                </div>
                <div class="mb-3">
                   <label for="exampleFormControlInput1" class="form-label">Email address</label>
                    <input type="email" class="form-control" id="exampleFormControlInput1" name="email" value={data.email} onChange={InputEvent} placeholder="name@example.com"/>
                </div>
                <div class="mb-3">
                   <label for="exampleFormControlInput1" class="form-label">Phone</label>
                    <input type="number" class="form-control" id="exampleFormControlInput1" name="phone" value={data.phone} onChange={InputEvent} placeholder="Enter Your Cell Number"/>
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlTextarea1" class="form-label">Message</label>
                    <textarea class="form-control" name="message" value={data.message} onChange={InputEvent} id="exampleFormControlTextarea1" rows="3"></textarea>
                </div>
                <div class="col-12">
                    <button class="btn btn-outline-primary" type="submit" >Submit form</button>
                </div>

                </form>

                </div>
            </div>
         </div>

        </>
    )
}

export default Contact;
